public boolean isPalindrome(String s) {
    StringBuilder reversed = new StringBuilder(s).reverse();
    return s.equals(reversed.toString());
}

bool isPalindrome(string s) {
    string reversed = s;
    reverse(reversed.begin(), reversed.end());
    return s == reversed;
}

