function firstNonRepeating(nums) {
  for (let i = 0; i < nums.length; i++) {
    let found = false;
    for (let j = 0; j < nums.length; j++) {
      if (i !== j && nums[i] === nums[j]) {
        found = true;
        break;
      }
    }
    if (!found) {
      return nums[i];
    }
  }
  return -1; // No non-repeating element found
}

