function reverseString(s) {
  const reversed = [];
  for (let i = s.length - 1; i >= 0; i--) {
    reversed.push(s[i]);
  }
  // Copy back to original
  for (let i = 0; i < s.length; i++) {
    s[i] = reversed[i];
  }
}

