def selection_sort(arr):
    n = len(arr)

    for i in range(n - 1):
        min_idx = i
        for j in range(i + 1, n):
            if arr[j] < arr[min_idx]:
                min_idx = j
        # Swap if minimum is not already at position i
        if min_idx != i:
            arr[i], arr[min_idx] = arr[min_idx], arr[i]

