function twoSum(nums, target) {
  for (let i = 0; i < nums.length; i++) {
    // Inner loop: pick remaining elements
    for (let j = i + 1; j < nums.length; j++) {
      // When i=0 (picking 2), j goes from 1 to 3
      // This picks: nums[1]=6, nums[2]=7, nums[3]=9
