function twoSum(nums: number[], target: number): number[] {
  const map: { [key: number]: number } = {};
  for (let i = 0; i < nums.length; i++) {
    const needed = target - nums[i];
    if (map[needed] !== undefined) {
      return [map[needed], i];
    }
    map[nums[i]] = i;
  }
  return [];
}
